/**
  * @protected
  * @override
  */
dvt.SequentialPlayable.prototype.Init = function(context, arPlayables)
{
  dvt.SequentialPlayable.superclass.Init.call(this, context);

  this._arPlayables = arPlayables ? arPlayables : [];
  this._currIndex = -1;
  this._bStarted = false;
};

