  dvt.ToolkitUtils.setAttrNullNS(rectElem, 'y', bounds.y);
  dvt.ToolkitUtils.setAttrNullNS(rectElem, 'width', bounds.w);
  dvt.ToolkitUtils.setAttrNullNS(rectElem, 'height', bounds.h);
