  var bboxDims = text.getDimensions();
  var padding = bboxDims.h * dvt.BackgroundOutputText._PADDING;
  return new dvt.Rect(context, bboxDims.x - padding, bboxDims.y, bboxDims.w + (2 * padding), bboxDims.h);
