      this._setStyleAttr(dvt.CSSStyle.BORDER_BOTTOM_WIDTH, null);
      this._setStyleAttr(dvt.CSSStyle.BORDER_RIGHT_WIDTH, null);
      this._setStyleAttr(dvt.CSSStyle.BORDER_LEFT_WIDTH, null);
