      this._setStyleAttr(dvt.CSSStyle.BORDER_RIGHT_WIDTH, bWidth);
      this._setStyleAttr(dvt.CSSStyle.BORDER_BOTTOM_WIDTH, dvt.CSSStyle._getBorderWidth(bwArray[2]));
