// Returns a CSSStyle object as a merge of the array
dvt.CSSStyle.mergeStyles = function(styleArray) {
  var style = new dvt.CSSStyle();
  if (styleArray) {
    for (var i = 0; i < styleArray.length; i++) {
      style.merge(styleArray[i]);
    }
  }
  return style;
};


