DvtCustomTooltip.TOOLTIP_ONLY = 'tooltipOnly';
DvtCustomTooltip.MENU_ONLY = 'menuOnly';
DvtCustomTooltip.MENU_AND_TOOLTIP = 'menuAndTooltip';
